				["start"] = {
				},
				["init"] = {
					["custom"] = "aura_env.playerGUID = UnitGUID(\"player\")\naura_env.spellID = 195452\naura_env.spellName = GetSpellInfo( aura_env.spellID )\naura_env.Applications = {}\naura_env.debuffModID = 197498\naura_env.spreaderID = 51505\n\naura_env.applicators = {\n    [aura_env.spellID] = GetSpellInfo(aura_env.spellID)\n}\n\nif select(4,GetTalentInfo(1,1,1)) then\n    aura_env.spreader = {[aura_env.spreaderID] = GetSpellInfo(aura_env.spreaderID)}\nelse\n    aura_env.spreader = {}\nend\n\n\n\naura_env.debuffMod = {\n    [aura_env.debuffModID] = {\n        name = GetSpellInfo( aura_env.debuffModID ),\n        stacks = false\n    },\n}\n\naura_env.Checker = function( unit )\n    \n    local output = \"\"\n    local guid = UnitGUID(unit)\n    \n    if not guid then\n        return \"no guid\"\n    elseif not UnitDebuff(unit, aura_env.spellName) then\n        return \"no fs\"\n    elseif not aura_env.Applications[guid] then\n        return \"no app info\"\n    end\n    \n    for k, v in pairs( aura_env.Applications[guid] ) do\n        output = output .. \" \" .. v\n    end\n    \n    return output\n    \nend\n\n\naura_env.buffCount = function ( unit, buff )\n    \n    local result = 0\n    \n    local guid = UnitGUID(unit)\n    \n    local aura = GetSpellInfo(buff)\n    \n    if not guid then\n        return -1\n    elseif not UnitDebuff(unit, aura_env.spellName) then\n        return -2\n    elseif not aura_env.Applications[guid] then\n        return -3\n    elseif not aura_env.Applications[guid][buff] then\n        return -4\n    end\n    \n    return aura_env.Applications[guid][buff] or 0\n    \nend\n\naura_env.applicationTime= 0\naura_env.applicationMod = {}",
