				["custom"] = "function ( event, timestamp, subtype, _, sourceGUID, sourceName, _, _, destGUID, destName, destFlags, _, spellID, spellName )\n    if select(4,GetTalentInfo(1,1,1)) then\n        aura_env.spreader = {[aura_env.spreaderID] = GetSpellInfo(aura_env.spreaderID)}\n    else\n        aura_env.spreader = {}\n    end     \n    if subtype == \"UNIT_DIED\" or subtype == \"UNIT_DESTROYED\" then\n        aura_env.Applications[ destGUID ] = nil\n        \n    elseif event == \"PLAYER_TARGET_CHANGED\" then\n        if not UnitDebuff( \"target\", aura_env.spellName, nil, \"PLAYER\" ) and aura_env.Applications[destGUID] then\n            aura_env.Applications[ destGUID ] = nil\n        end\n    elseif event == \"UNIT_SPELLCAST_SUCCEEDED\" and timestamp == \"player\" then\n        -- timestamp is actually unit for this event.\n        -- sourceName is actually spellID for this event.\n        \n        if aura_env.applicators[ sourceName ] then\n            -- count our buffs, start watching for debuff applications.\n            aura_env.applicationTime= GetTime()            \n            for k, v in pairs( aura_env.debuffMod ) do\n                if v.stacks then\n                    _, _, _, aura_env.applicationMod[ k ] = UnitBuff( \"player\", v.name )                    \n                    aura_env.applicationMod[ k ] = aura_env.applicationMod[ k ] or 0               \n                else\n                    aura_env.applicationMod[ k ] = UnitBuff( \"player\", v.name ) and 1 or 0\n                    \n                end\n                -- print(k .. \": \" .. aura_env.applicationMod[ k ])\n            end\n            -- print(\"Used applicator \" .. sourceName .. \".\")            \n        end\n        \n    elseif event == \"COMBAT_LOG_EVENT_UNFILTERED\" and sourceGUID == aura_env.playerGUID then\n        if ( subtype == \"SPELL_CAST_SUCCESS\") and aura_env.spreader[ spellID ] and aura_env.Applications[ destGUID ] then\n            -- get the applicationMods from our target's application.\n            aura_env.applicationMod = aura_env.Applications[ destGUID ]\n            aura_env.applicationTime= GetTime()\n            \n            --print(\"Spreader hit \" .. spellID .. \".\")\n        end\n        if spellID == aura_env.spellID then\n            if subtype == 'SPELL_AURA_APPLIED' or subtype == 'SPELL_AURA_REFRESH' or subtype == 'SPELL_AURA_APPLIED_DOSE' then\n                -- check if we are in the window following an application ability.\n                if GetTime() - aura_env.applicationTime< 0.500 then\n                    aura_env.Applications[ destGUID ] = aura_env.Applications[ destGUID ] or {}\n                    for k, v in pairs(aura_env.applicationMod ) do\n                        aura_env.Applications[destGUID][k]=v\n                    end\n                    --print(\"Applied debuff within window.\")                \n                end      \n            elseif subtype == 'SPELL_AURA_REMOVED' or subtype == 'SPELL_AURA_BROKEN' or subtype == 'SPELL_AURA_BROKEN_SPELL' then\n                aura_env.Applications[ destGUID ] = nil\n                -- print(\"Debuff expired.\")\n            end\n            \n        end\n        \n    end\n    \n    return aura_env.buffCount and ( aura_env.buffCount( \"target\", aura_env.debuffModID) > 0 ) or false\nend",
