				},
				["init"] = {
					["custom"] = "---------------------------------------\n--- Original Code written by Hamsda ---\n---    https://wago.io/NJKRRlT9W    ---\n--- Modified by Rehok @ Tarren Mill ---\n---------------------------------------\n\n\naura_env.bulwark = 209389 -- Bulwark of Order -- Paladin\naura_env.veng = 184662 -- Shield of Vengeance -- Ret Paladin\naura_env.ignore = 190456 -- Ignore Pain -- Warrior\naura_env.blood = 48707 -- Blood Shield -- Death Knight\naura_env.soul = 48707 -- Soul Barrier -- Death Knight\n\n\naura_env.shortenNumber = function(number)\n    local affixes = {\n        \"\",\n        \"K\",\n        \"M\",\n        \"B\",\n    }\n    \n    local affix = 1\n    local dec = 0\n    local num1 = math.abs(number)\n    while num1 >= 1000 and affix < #affixes do\n        num1 = num1 / 1000\n        affix = affix + 1\n    end\n    if affix > 1 then\n        dec = 0\n        local num2 = num1\n        while num2 >= 10 do\n            num2 = num2 / 10\n            \n        end\n        if affix > 2 then\n            dec = 1\n            local num2 = num1\n            while num2 >= 100 do\n                num2 = num2 /10\n            end\n        end\n        \n        \n    end\n    if number < 0 then\n        num1 = -num1\n    end\n    \n    return string.format(\"%.\"..dec..\"f\"..affixes[affix], num1)\nend\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n",
