		["Kil'Jaeden - Lingering eruption soon targets - raid frames"] = {
			["outline"] = "OUTLINE",
			["xOffset"] = 0,
			["displayText"] = "%c",
			["customText"] = "function()\n    if not aura_env.running or WeakAuras.IsOptionsOpen() then\n        return \n    end\n    \n    if aura_env.last and (GetTime() - aura_env.last) < 0.3 then\n        return\n    end\n    \n    aura_env.last = GetTime()\n    \n    --Go through and update the health of erupting reflections HP\n    for _, unit in pairs(aura_env.units) do\n        if UnitExists(unit) then \n            local playerName = aura_env.unitIsMob(unit)\n            \n            if playerName then\n                aura_env.debuffed[playerName].health = aura_env.round((UnitHealth(unit) / UnitHealthMax(unit)) * 100)\n            end\n        end\n    end\n    \n    --Go through each of the debuffed players and see if their mob is below 100%, if so the hightlight them\n    for name, data in pairs(aura_env.debuffed) do\n        if tonumber(data.health) < 100 and not data.hightlighted then\n            aura_env.highlight(name)\n            data.hightlighted = true\n        end\n        \n        if UnitIsDead(name) then\n            if data.hightlighted then\n                aura_env.removeHighlight(name)\n            end\n            \n            aura_env.debuffed[name] = nil\n        end\n    end\nend",
			["yOffset"] = 0,
			["anchorPoint"] = "CENTER",
			["activeTriggerMode"] = -10,
			["customTextUpdate"] = "update",
			["url"] = "https://wago.io/H1YLH9iI-/2",
			["actions"] = {
				["start"] = {
				},
				["init"] = {
					["custom"] = "--The position of the icon on the raid frames\n--Available position options: \"CENTER\", \"BOTTOM\", \"TOP\", \"LEFT\", \"RIGHT\", \"BOTTOMLEFT\", \"BOTTOMRIGHT\", \"TOPLEFT\", \"TOPRIGHT\"\naura_env.position = \"CENTER\"\n--The height and width of the icon to show on the raid frames\naura_env.iconHeight = 40\naura_env.iconWidth = 40\n\n--The alpha of the icon, 1 is max 0 is minimum. The lower the number the more faded out it will be.\naura_env.iconAlpha = 0.8\n\n--DO NOT EDIT BELOW--\naura_env.spellId = 221771\naura_env.debuffDuration = 5\n\naura_env.reset = function()\n    aura_env.frames = {}\n    aura_env.debuffed = {}\nend\n\naura_env.reset()\naura_env.mobName = \"'s Erupting Reflection\"\n\naura_env.round = function(num, numDecimalPlaces)\n    local mult = 10^(numDecimalPlaces or 0)\n    return math.floor(num * mult + 0.5) / mult\nend\n\naura_env.removeHighlight = function(target)\n    aura_env.frames[target]:Hide()\n    aura_env.frames[target] = nil\nend\n\naura_env.unitIsMob = function(unit)\n    local unitName = UnitName(unit)\n    for name, player in pairs(aura_env.debuffed) do\n        if player.mobName == unitName then\n            return name\n        end\n    end\n    \n    return false\nend\n\naura_env.addIcon = function(parentFrame, target)\n    local frame = CreateFrame(\"Frame\",nil,parentFrame)\n    frame:SetFrameStrata(\"HIGH\")\n    frame:SetWidth(aura_env.iconWidth)\n    frame:SetHeight(aura_env.iconHeight)\n    frame:SetAlpha(aura_env.iconAlpha)\n    \n    local texture = frame:CreateTexture(nil,\"HIGH\")\n    texture:SetTexture(select(3, GetSpellInfo(aura_env.spellId)))\n    texture:SetAllPoints(frame)\n    frame.texture = texture\n    \n    frame:SetPoint(aura_env.position,0,0)\n    frame:Show()\n    \n    aura_env.frames[target] = frame\nend\n\naura_env.highlight = function(target)\n    local hasGrid2 = IsAddOnLoaded(\"Grid2\")\n    local hasElvUI = _G[\"ElvUF_Raid\"] and _G[\"ElvUF_Raid\"]:IsVisible()\n    \n    if hasElvUI then\n        for i=1, 8 do\n            for j=1, 5 do\n                local f = _G[\"ElvUF_RaidGroup\"..i..\"UnitButton\"..j]\n                if f and f.unit and UnitName(f.unit) == target then\n                    aura_env.addIcon(f, target)\n                    return\n                end\n            end\n        end\n    elseif hasGrid2 then\n        local layout = Grid2LayoutFrame\n        \n        if layout then\n            local children = {layout:GetChildren()}\n            for _, child in ipairs(children) do\n                if child:IsVisible() then\n                    local frames = {child:GetChildren()}\n                    for _, f in ipairs(frames) do\n                        if f.unit and UnitName(f.unit) == target then\n                            aura_env.addIcon(f, target)\n                            return\n                        end\n                    end\n                end\n            end\n        end\n    else\n        for i=1, 40 do\n            local f = _G[\"CompactRaidFrame\"..i]\n            if f and f.unitExists and f.unit and UnitName(f.unit) == target then\n                aura_env.addIcon(f, target)\n                return\n            end\n        end\n        for i=1, 4 do\n            for j=1, 5 do\n                local f = _G[\"CompactRaidGroup\"..i..\"Member\"..j]\n                if f and f.unitExists and f.unit and UnitName(f.unit) == target then\n                    aura_env.addIcon(f, target)\n                    return\n                end\n            end\n        end\n    end\nend",
