				["custom"] = "function(e, ts, subEvent, hideCaster, sourceGuid, sourceName, sourceFlags, sourceRaidFlags, destGuid, destName, destFlags, destRaidFlags, spellId, ...)\n    \n    if subEvent == \"SPELL_AURA_REMOVED\" and spellId == 236710 then\n        \n        aura_env.debuffed[destName] = {mobName = destName .. aura_env.mobName, health = 100}\n        aura_env.running = true\n        \n        --Make a table of the units we are going to be iterating through in the other function\n        aura_env.units = {\n            \"target\"\n        }\n        \n        for i=1,10 do\n            aura_env.units[#aura_env.units + 1] = \"nameplate\"..i\n        end\n        \n        for i=1, GetNumGroupMembers() do\n            aura_env.units[#aura_env.units + 1] = \"raid\"..i..\"target\"\n        end\n    elseif subEvent == \"SPELL_AURA_REMOVED\" and spellId == 243536 then\n        --lingering eruption removed\n        aura_env.removeHighlight(destName)\n        aura_env.debuffed[destName] = nil\n        \n    elseif e == \"ENCOUNTER_START\" or e == \"ENCOUNTER_END\" then\n        \n        for player, frame in pairs(aura_env.frames) do\n            aura_env.removedHighlight(player)\n        end\n        \n        aura_env.reset()\n        \n    end\nend",
