					["custom"] = "local LBG = LibStub(\"LibButtonGlow-1.0\")\nfor k,v in pairs(aura_env.frames) do\n    LBG.HideOverlayGlow(v.frame)\nend\n\n\n",
					["do_custom"] = true,
